void CSplashView1::ConstructL()
    {
    BaseConstructL();
    }

// ---------------------------------------------------------
// CSplashView1::~CSplashView1()
// 
// ---------------------------------------------------------
//
CSplashView1::~CSplashView1()
    {
    if ( iContainer )
        {
        AppUi()->RemoveFromViewStack( *this, iContainer );
        }

    delete iContainer;
	iContainer = NULL;
    }


// ---------------------------------------------------------
// TUid CSplashView1::Id()
// 
