//
TUid CSplashView1::Id() const
    {
    return KView4Id;
    }

// ---------------------------------------------------------
// CSplashView1::HandleCommandL(TInt aCommand)
// 
// ---------------------------------------------------------
//
void CSplashView1::HandleCommandL(TInt aCommand)
    {
	AppUi()->HandleCommandL( aCommand );
    }

// ---------------------------------------------------------
// CSplashView1::HandleClientRectChange()
// ---------------------------------------------------------
//
void CSplashView1::HandleClientRectChange()
    {
    if ( iContainer )
        {
        iContainer->SetRect( ClientRect() );
        }
    }

// ---------------------------------------------------------
// CSplashView1::DoActivateL()
// 
// ---------------------------------------------------------
//
void CSplashView1::DoActivateL(
   const TVwsViewId& /*aPrevViewId*/,TUid /*aCustomMessageId*/,
   const TDesC8& /*aCustomMessage*/)
    {
    if (!iContainer)
        {
        iContainer = new (ELeave) CSplashContainer1;
        iContainer->SetMopParent(this);
        iContainer->ConstructL(AppUi()->ApplicationRect());
		iContainer->SetView(this);
        AppUi()->AddToStackL( *this, iContainer );
        }
   }

// ---------------------------------------------------------
// CSplashView1::HandleCommandL(TInt aCommand)
//
// ---------------------------------------------------------
//
void CSplashView1::DoDeactivate()
    {
    if ( iContainer )
        {
        AppUi()->RemoveFromViewStack( *this, iContainer );
        }
    
    delete iContainer;
    iContainer = NULL;
    }

// ---------------------------------------------------------
// CSonarBlueView::SetAppUi(CSonarBlueAppUi* aAppUi)
// 
// ---------------------------------------------------------
//
void CSplashView1::SetAppUi(CTOPGUNAppUi* aAppUi, CAknTabGroup* aTabGroup)
	{
	iAppUi = aAppUi;
	iTabGroup = aTabGroup;
	}

void CSplashView1::SwitchView()
	{
	iAppUi->SwitchView(0);
	}

