// ---------------------------------------------------------
// CTOPGUNView2::~CTOPGUNView2()
// destructor
// ---------------------------------------------------------
//
CTOPGUNView2::~CTOPGUNView2()
    {
    if ( iContainer )
        {
        AppUi()->RemoveFromViewStack( *this, iContainer );
        }

    delete iContainer;
	//delete iLevelLoader;
    }

