// ---------------------------------------------------------
// CTOPGUNView4::ConstructL(const TRect& aRect)
// EPOC two-phased constructor
// ---------------------------------------------------------
//
void CTOPGUNView4::ConstructL()
    {
    BaseConstructL();
    }

// ---------------------------------------------------------
// CTOPGUNView4::~CTOPGUNView4()
// destructor
// ---------------------------------------------------------
//
CTOPGUNView4::~CTOPGUNView4()
    {
    if ( iContainer )
        {
        AppUi()->RemoveFromViewStack( *this, iContainer );
        }

    delete iContainer;
    }

// ---------------------------------------------------------
// TUid CTOPGUNView4::Id()
//
// ---------------------------------------------------------
//
TUid CTOPGUNView4::Id() const
    {
    return KView6Id;
    }

// ---------------------------------------------------------
// CTOPGUNView4::HandleCommandL(TInt aCommand)
// takes care of view command handling
// ---------------------------------------------------------
//
void CTOPGUNView4::HandleCommandL(TInt aCommand)
    {   
    switch ( aCommand )
        {
		case EStartGame:
			AppUi()->HandleCommandL(EStartGame);
			break;
        case EAknSoftkeyBack:
            {
            //AppUi()->HandleCommandL(EEikCmdExit);
            break;
            }
        default:
            {
            AppUi()->HandleCommandL( aCommand );
            break;
            }
        }
    }

// ---------------------------------------------------------
// CTOPGUNView4::HandleClientRectChange()
// ---------------------------------------------------------
//
void CTOPGUNView4::HandleClientRectChange()
    {
    if ( iContainer )
        {
        iContainer->SetRect( ClientRect() );
        }
    }

// ---------------------------------------------------------
// CTOPGUNView4::DoActivateL(...)
// 
// ---------------------------------------------------------
//
void CTOPGUNView4::DoActivateL(
   const TVwsViewId& /*aPrevViewId*/,TUid /*aCustomMessageId*/,
   const TDesC8& /*aCustomMessage*/)
    {
    if (!iContainer)
        {
        iContainer = new (ELeave) CTOPGUNContainer4;
        iContainer->SetMopParent(this);
        iContainer->ConstructL(AppUi()->ApplicationRect());
        AppUi()->AddToStackL( *this, iContainer );
		iContainer->SetView(this);
        } 
   }

// ---------------------------------------------------------
// CTOPGUNView4::DoDeactivate()
// 
// ---------------------------------------------------------
//
void CTOPGUNView4::DoDeactivate()
    {
    if ( iContainer )
        {
        AppUi()->RemoveFromViewStack( *this, iContainer );
        }
    
    delete iContainer;
    iContainer = NULL;
    }

// ---------------------------------------------------------
// CTOPGUNView::SetAppUi(CTOPGUNAppUi* aAppUi)
// 
// ---------------------------------------------------------
//
void CTOPGUNView4::SetAppUi(CTOPGUNAppUi* aAppUi)
	{
	iAppUi = aAppUi;
	}

// ----------------------------------------------------
// CTOPGUNView::SwitchView(TInt aIndex)
// 
// ----------------------------------------------------
//
void CTOPGUNView4::SwitchView(TInt aIndex)
	{
	if (iAppUi)
		{
		iAppUi->SwitchView(aIndex);	
		}
	}


